import React from 'react';
import { 
    Paper, 
    Typography, 
    List, 
    ListItem, 
    ListItemText,
    Chip,
    Box,
    Divider
} from '@mui/material';
import { Timeline, TimelineItem, TimelineSeparator, TimelineConnector, TimelineContent, TimelineDot } from '@mui/lab';

interface CriticalPathDisplayProps {
    criticalPath: number[];
    tasks: {
        id: number;
        duration: number;
        early: number;
        late: number;
        margin: number;
    }[];
}

const CriticalPathDisplay: React.FC<CriticalPathDisplayProps> = ({ criticalPath, tasks }) => {
    const getTotalDuration = () => {
        return criticalPath.reduce((total, taskId) => {
            const task = tasks.find(t => t.id === taskId);
            return total + (task?.duration || 0);
        }, 0);
    };

    return (
        <Paper sx={{ p: 3, mb: 3 }}>
            <Typography variant="h6" gutterBottom>
                Critical Path Analysis
            </Typography>
            <Box sx={{ display: 'flex', gap: 2, mb: 2 }}>
                <Chip 
                    label={`Total Duration: ${getTotalDuration()} units`}
                    color="primary"
                    variant="outlined"
                />
                <Chip 
                    label={`${criticalPath.length} Tasks`}
                    color="secondary"
                    variant="outlined"
                />
            </Box>
            <Divider sx={{ my: 2 }} />
            <Timeline position="alternate">
                {criticalPath.map((taskId, index) => {
                    const task = tasks.find(t => t.id === taskId);
                    if (!task) return null;

                    return (
                        <TimelineItem key={taskId}>
                            <TimelineSeparator>
                                <TimelineDot color="primary" />
                                {index < criticalPath.length - 1 && <TimelineConnector />}
                            </TimelineSeparator>
                            <TimelineContent>
                                <Paper elevation={3} sx={{ p: 2 }}>
                                    <Typography variant="h6" component="h1">
                                        Task {task.id}
                                    </Typography>
                                    <Typography>Duration: {task.duration}</Typography>
                                    <Typography>Early Start: {task.early}</Typography>
                                    <Typography>Late Start: {task.late}</Typography>
                                    <Typography color="error">
                                        Margin: {task.margin}
                                    </Typography>
                                </Paper>
                            </TimelineContent>
                        </TimelineItem>
                    );
                })}
            </Timeline>
        </Paper>
    );
};

export default CriticalPathDisplay;
