import React, { useState } from 'react';
import { 
  Container, 
  CssBaseline, 
  ThemeProvider, 
  createTheme, 
  CircularProgress,
  Alert,
  Box
} from '@mui/material';
import FileSelector from './components/FileSelector';
import GraphDisplay from './components/GraphDisplay';
import ResultsTable from './components/ResultsTable';
import { fetchGraphData } from './api/graphApi';
import './styles/fonts.css';

const theme = createTheme({
  typography: {
    fontFamily: 'NoiGrotesk, Arial',
  },
  palette: {
    primary: {
      main: '#2C3E50',
    },
    background: {
      default: '#f5f5f5',
    },
  },
});

interface GraphData {
  graph: {
    nodes: any[];
    edges: any[];
  };
  ranks: any[];
  calendar: any[];
  criticalPath: string[];
  hasCircuit: boolean;
}

function App() {
  const [selectedFile, setSelectedFile] = useState<number | null>(null);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [graphData, setGraphData] = useState<GraphData | null>(null);

  const handleFileSelect = async (fileNumber: number) => {
    setLoading(true);
    setError(null);
    setSelectedFile(fileNumber);

    try {
      const data = await fetchGraphData(fileNumber);
      setGraphData(data);
    } catch (err) {
      setError('Erreur lors du chargement des données. Veuillez réessayer.');
      console.error('Error fetching graph data:', err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Container maxWidth="lg" sx={{ py: 4 }}>
        <FileSelector onFileSelect={handleFileSelect} />

        {loading && (
          <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
            <CircularProgress />
          </Box>
        )}

        {error && (
          <Alert severity="error" sx={{ mt: 2 }}>
            {error}
          </Alert>
        )}

        {graphData && (
          <>
            {graphData.hasCircuit && (
              <Alert severity="warning" sx={{ mt: 2 }}>
                Attention : Un circuit a été détecté dans le graphe !
              </Alert>
            )}

            <GraphDisplay 
              graphData={graphData.graph}
              criticalPath={graphData.criticalPath}
            />

            <ResultsTable 
              tasks={graphData.ranks}
              type="ranks"
              criticalPath={graphData.criticalPath}
            />

            <ResultsTable 
              tasks={graphData.calendar}
              type="calendar"
              criticalPath={graphData.criticalPath}
            />
          </>
        )}
      </Container>
    </ThemeProvider>
  );
}

export default App;
