import React from 'react';
import { Paper, Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Typography } from '@mui/material';

interface CalendarDisplayProps {
    calendar: {
        early_dates: number[];
        late_dates: number[];
        total_margins: number[];
        free_margins: number[];
    };
}

const CalendarDisplay: React.FC<CalendarDisplayProps> = ({ calendar }) => {
    if (!calendar) return null;

    const { early_dates, late_dates, total_margins, free_margins } = calendar;
    const tasks = Array.from({ length: early_dates.length }, (_, i) => 
        i === 0 ? 'α' : i === early_dates.length - 1 ? 'ω' : i.toString()
    );

    return (
        <Paper sx={{ margin: 2, padding: 2 }}>
            <Typography variant="h6" gutterBottom>
                Calendrier et Marges
            </Typography>
            <TableContainer>
                <Table size="small">
                    <TableHead>
                        <TableRow>
                            <TableCell>Tâche</TableCell>
                            <TableCell align="center">Date au plus tôt</TableCell>
                            <TableCell align="center">Date au plus tard</TableCell>
                            <TableCell align="center">Marge totale</TableCell>
                            <TableCell align="center">Marge libre</TableCell>
                        </TableRow>
                    </TableHead>
                    <TableBody>
                        {tasks.map((task, index) => (
                            <TableRow key={index}>
                                <TableCell>{task}</TableCell>
                                <TableCell align="center">{early_dates[index]}</TableCell>
                                <TableCell align="center">{late_dates[index]}</TableCell>
                                <TableCell align="center">{total_margins[index]}</TableCell>
                                <TableCell align="center">{free_margins[index]}</TableCell>
                            </TableRow>
                        ))}
                    </TableBody>
                </Table>
            </TableContainer>
        </Paper>
    );
};

export default CalendarDisplay;
