import React from 'react';
import { Box, Typography } from '@mui/material';
import { motion } from 'framer-motion';
import { useInView } from 'react-intersection-observer';

interface ImmersiveSectionProps {
  title: string;
  subtitle?: string;
  children: React.ReactNode;
  gradient?: string;
}

export const ImmersiveSection: React.FC<ImmersiveSectionProps> = ({
  title,
  subtitle,
  children,
  gradient = 'linear-gradient(45deg, rgba(33,150,243,0.1), rgba(245,0,87,0.1))'
}) => {
  const [ref, inView] = useInView({
    threshold: 0.3,
    triggerOnce: false
  });

  return (
    <Box
      component={motion.div}
      ref={ref}
      initial={{ opacity: 0, y: 100 }}
      animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 100 }}
      transition={{ duration: 0.8, ease: [0.6, 0.05, -0.01, 0.9] }}
      sx={{
        minHeight: '100vh',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        position: 'relative',
        overflow: 'hidden',
        '&::before': {
          content: '""',
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: gradient,
          opacity: 0.5,
          zIndex: 0,
        }
      }}
    >
      <Box
        sx={{
          position: 'relative',
          zIndex: 1,
          px: { xs: 2, sm: 4, md: 8 },
          py: { xs: 4, sm: 6, md: 8 },
        }}
      >
        <Typography
          variant="h1"
          component={motion.h1}
          initial={{ opacity: 0, y: 50 }}
          animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 50 }}
          transition={{ duration: 0.8, ease: [0.16, 1, 0.3, 1] }}
          sx={{
            mb: 2,
            background: 'linear-gradient(45deg, #2196f3, #f50057)',
            backgroundClip: 'text',
            WebkitBackgroundClip: 'text',
            color: 'transparent',
            textAlign: 'center',
          }}
        >
          {title}
        </Typography>
        {subtitle && (
          <Typography
            variant="h2"
            component={motion.h2}
            initial={{ opacity: 0, y: 30 }}
            animate={inView ? { opacity: 1, y: 0 } : { opacity: 0, y: 30 }}
            transition={{ duration: 0.6, delay: 0.3 }}
            sx={{
              mb: 6,
              color: 'text.secondary',
              textAlign: 'center',
            }}
          >
            {subtitle}
          </Typography>
        )}
        <Box
          component={motion.div}
          initial={{ opacity: 0, scale: 0.95 }}
          animate={inView ? { opacity: 1, scale: 1 } : { opacity: 0, scale: 0.95 }}
          transition={{ duration: 0.6, delay: 0.4 }}
          sx={{
            position: 'relative',
            '&::before': {
              content: '""',
              position: 'absolute',
              top: -2,
              left: -2,
              right: -2,
              bottom: -2,
              background: 'linear-gradient(45deg, #2196f3, #f50057)',
              borderRadius: '18px',
              zIndex: -1,
              opacity: 0.5,
            }
          }}
        >
          {children}
        </Box>
      </Box>
    </Box>
  );
};
