import React from 'react';
import { Box, Paper, Typography } from '@mui/material';
import Network from 'react-vis-network-graph';
import { Node, Edge } from '../types/graphTypes';

interface GraphDisplayProps {
    graphData: {
        nodes: Node[];
        edges: Edge[];
    };
    criticalPath: string[];
}

const GraphDisplay: React.FC<GraphDisplayProps> = ({ graphData, criticalPath }) => {
    const options = {
        nodes: {
            shape: 'box',
            font: {
                face: 'NoiGrotesk',
                size: 14
            },
            borderWidth: 2,
            color: {
                background: '#ffffff',
                border: '#2C3E50',
                highlight: {
                    background: '#EDF2F7',
                    border: '#2C3E50'
                }
            }
        },
        edges: {
            arrows: {
                to: {
                    enabled: true,
                    scaleFactor: 1
                }
            },
            color: {
                color: '#2C3E50',
                highlight: '#718096'
            },
            font: {
                face: 'NoiGrotesk',
                size: 12
            }
        },
        physics: {
            enabled: false
        },
        layout: {
            hierarchical: {
                enabled: true,
                direction: 'LR',
                sortMethod: 'directed',
                levelSeparation: 200
            }
        }
    };

    // Mettre en évidence le chemin critique
    const highlightedGraphData = {
        nodes: graphData.nodes.map(node => ({
            ...node,
            color: criticalPath.includes(node.id) ? {
                background: '#FEB2B2',
                border: '#E53E3E',
                highlight: {
                    background: '#FC8181',
                    border: '#C53030'
                }
            } : node.color
        })),
        edges: graphData.edges.map(edge => ({
            ...edge,
            color: criticalPath.includes(edge.from) && criticalPath.includes(edge.to) ? {
                color: '#E53E3E',
                highlight: '#C53030'
            } : edge.color
        }))
    };

    return (
        <Paper elevation={2} sx={{ p: 2, my: 2 }}>
            <Typography variant="h6" gutterBottom>
                Graphe d'ordonnancement
            </Typography>
            <Box sx={{ height: '500px', border: '1px solid #E2E8F0', borderRadius: 1 }}>
                <Network
                    graph={highlightedGraphData}
                    options={options}
                />
            </Box>
        </Paper>
    );
};

export default GraphDisplay;
