import React, { useState } from 'react';
import { 
  Container, 
  CssBaseline, 
  ThemeProvider, 
  createTheme,
  Box,
  AppBar,
  Toolbar,
  Typography,
  Stepper,
  Step,
  StepLabel,
  Paper,
  Button,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  CircularProgress,
  Alert,
  Snackbar
} from '@mui/material';
import { Menu, Upload, Timeline, Schedule, Info } from '@mui/icons-material';
import FileSelector from './components/FileSelector';
import GraphDisplay from './components/GraphDisplay';
import ResultsTable from './components/ResultsTable';
import MatrixDisplay from './components/MatrixDisplay';
import CalendarDisplay from './components/CalendarDisplay';
import { fetchGraphData } from './api/graphApi';
import { GraphData } from './types/graphTypes';
import './styles/fonts.css';

const theme = createTheme({
  typography: {
    fontFamily: 'NoiGrotesk, Arial',
    h1: {
      fontSize: '2.5rem',
      fontWeight: 500,
      letterSpacing: '-0.02em'
    },
    h2: {
      fontSize: '2rem',
      fontWeight: 500,
      letterSpacing: '-0.01em'
    },
    h6: {
      fontSize: '1.25rem',
      fontWeight: 500
    },
    body1: {
      fontSize: '1rem',
      lineHeight: 1.5
    }
  },
  palette: {
    primary: {
      main: '#2C3E50',
      light: '#34495E',
      dark: '#2C3E50'
    },
    secondary: {
      main: '#E74C3C',
      light: '#FC8181',
      dark: '#C53030'
    },
    background: {
      default: '#F7FAFC',
      paper: '#FFFFFF'
    }
  },
  shape: {
    borderRadius: 8
  },
  components: {
    MuiButton: {
      styleOverrides: {
        root: {
          textTransform: 'none',
          fontWeight: 500,
          padding: '8px 16px'
        }
      }
    },
    MuiPaper: {
      styleOverrides: {
        root: {
          boxShadow: '0 4px 6px -1px rgba(0, 0, 0, 0.1), 0 2px 4px -1px rgba(0, 0, 0, 0.06)'
        }
      }
    }
  }
});

const steps = ['Sélection du fichier', 'Analyse du graphe', 'Résultats'];

const App: React.FC = () => {
  const [activeStep, setActiveStep] = useState(0);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [graphData, setGraphData] = useState<GraphData | null>(null);
  const [snackbarOpen, setSnackbarOpen] = useState(false);
  const [snackbarMessage, setSnackbarMessage] = useState('');

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  const handleFileSelect = async (file: File) => {
    setLoading(true);
    setError(null);
    try {
      const data = await fetchGraphData(file);
      setGraphData(data);
      setActiveStep(1); 
      setSnackbarMessage('Fichier chargé avec succès !');
      setSnackbarOpen(true);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'Une erreur est survenue');
      console.error('Error fetching graph data:', err);
    } finally {
      setLoading(false);
    }
  };

  const handleNext = () => {
    setActiveStep((prevStep) => prevStep + 1);
  };

  const handleBack = () => {
    setActiveStep((prevStep) => prevStep - 1);
  };

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <Box sx={{ display: 'flex' }}>
        <AppBar position="fixed" sx={{ zIndex: (theme) => theme.zIndex.drawer + 1 }}>
          <Toolbar>
            <IconButton
              color="inherit"
              aria-label="open drawer"
              edge="start"
              onClick={toggleDrawer}
              sx={{ mr: 2 }}
            >
              <Menu />
            </IconButton>
            <Typography variant="h6" noWrap component="div" sx={{ flexGrow: 1 }}>
              Graph Scheduling
            </Typography>
          </Toolbar>
        </AppBar>

        <Drawer
          variant="temporary"
          open={drawerOpen}
          onClose={toggleDrawer}
          sx={{
            width: 240,
            flexShrink: 0,
            '& .MuiDrawer-paper': {
              width: 240,
              boxSizing: 'border-box',
            },
          }}
        >
          <Toolbar />
          <Box sx={{ overflow: 'auto' }}>
            <List>
              <ListItem button onClick={() => setActiveStep(0)}>
                <ListItemIcon>
                  <Upload />
                </ListItemIcon>
                <ListItemText primary="Sélection du fichier" />
              </ListItem>
              <ListItem button onClick={() => setActiveStep(1)} disabled={!graphData}>
                <ListItemIcon>
                  <Timeline />
                </ListItemIcon>
                <ListItemText primary="Analyse du graphe" />
              </ListItem>
              <ListItem button onClick={() => setActiveStep(2)} disabled={!graphData}>
                <ListItemIcon>
                  <Schedule />
                </ListItemIcon>
                <ListItemText primary="Résultats" />
              </ListItem>
            </List>
          </Box>
        </Drawer>

        <Box component="main" sx={{ flexGrow: 1, p: 3 }}>
          <Toolbar />
          <Container maxWidth="xl" sx={{ mt: 4, mb: 4 }}>
            {loading ? (
              <Box sx={{ display: 'flex', justifyContent: 'center', mt: 4 }}>
                <CircularProgress />
              </Box>
            ) : (
              <Box>
                {activeStep === 0 && (
                  <FileSelector onFileSelect={handleFileSelect} />
                )}
                {activeStep === 1 && graphData && (
                  <GraphDisplay data={graphData} />
                )}
                {activeStep === 2 && graphData && (
                  <Box>
                    <Paper sx={{ p: 2, mb: 2 }}>
                      <Typography variant="h6" gutterBottom>
                        Résultat de l'analyse
                      </Typography>
                      <Alert severity={graphData.hasCircuit ? "error" : "success"} sx={{ mb: 2 }}>
                        {graphData.hasCircuit 
                          ? "Circuit détecté ! Ce n'est pas un graphe d'ordonnancement valide."
                          : "Pas de circuit détecté. C'est un graphe d'ordonnancement valide."}
                      </Alert>
                    </Paper>

                    <MatrixDisplay matrix={graphData.matrix} />
                    <CalendarDisplay calendar={graphData.calendar} />

                    <Paper sx={{ p: 2, mb: 2, minHeight: '500px' }}>
                      <Typography variant="h6" gutterBottom>
                        Visualisation du graphe
                      </Typography>
                      <GraphDisplay data={graphData} />
                    </Paper>
                  </Box>
                )}
              </Box>
            )}
          </Container>
          <Container maxWidth="lg">
            <Paper elevation={0} sx={{ p: 3, mb: 3 }}>
              <Stepper activeStep={activeStep} sx={{ mb: 4 }}>
                {steps.map((label) => (
                  <Step key={label}>
                    <StepLabel>{label}</StepLabel>
                  </Step>
                ))}
              </Stepper>
              <Box sx={{ display: 'flex', justifyContent: 'space-between', mt: 4 }}>
                <Button
                  onClick={handleBack}
                  disabled={activeStep === 0}
                  variant="outlined"
                >
                  Retour
                </Button>
                <Button
                  onClick={handleNext}
                  disabled={activeStep === 2 || !graphData}
                  variant="contained"
                >
                  Suivant
                </Button>
              </Box>
            </Paper>
          </Container>
        </Box>
      </Box>

      <Snackbar
        open={snackbarOpen}
        autoHideDuration={6000}
        onClose={() => setSnackbarOpen(false)}
        message={snackbarMessage}
      />
    </ThemeProvider>
  );
}

export default App;
