import { createTheme, ThemeOptions } from '@mui/material/styles';

const getTheme = (): ThemeOptions => ({
  palette: {
    mode: 'dark',
    primary: {
      main: '#2196f3',
      light: '#64b5f6',
      dark: '#1976d2',
    },
    secondary: {
      main: '#f50057',
      light: '#ff4081',
      dark: '#c51162',
    },
    background: {
      default: '#030303',
      paper: 'rgba(30, 30, 30, 0.8)',
    },
    text: {
      primary: '#ffffff',
      secondary: 'rgba(255, 255, 255, 0.7)',
    }
  },
  typography: {
    fontFamily: '"NoiGrotesk", "Helvetica", "Arial", sans-serif',
    h1: {
      fontSize: 'clamp(2.5rem, 8vw, 6rem)',
      fontWeight: 500,
      fontFamily: '"NoiGrotesk", "Helvetica", "Arial", sans-serif',
      letterSpacing: '-0.02em',
      lineHeight: 1.2,
    },
    h2: {
      fontSize: 'clamp(2rem, 6vw, 4rem)',
      fontWeight: 500,
      fontFamily: '"NoiGrotesk", "Helvetica", "Arial", sans-serif',
      letterSpacing: '-0.02em',
    },
    h6: {
      fontSize: '1.25rem',
      fontWeight: 500,
      fontFamily: '"NoiGrotesk", "Helvetica", "Arial", sans-serif',
    },
    body1: {
      fontFamily: '"NoiGrotesk", "Helvetica", "Arial", sans-serif',
      fontSize: '1.125rem',
    },
    body2: {
      fontFamily: '"NoiGrotesk", "Helvetica", "Arial", sans-serif',
    },
    button: {
      fontFamily: '"NoiGrotesk", "Helvetica", "Arial", sans-serif',
      textTransform: 'none',
    },
  },
  components: {
    MuiPaper: {
      styleOverrides: {
        root: {
          backgroundImage: 'none',
          backdropFilter: 'blur(10px)',
          backgroundColor: 'rgba(30, 30, 30, 0.8)',
          borderRadius: 16,
        },
      },
    },
    MuiAppBar: {
      styleOverrides: {
        root: {
          background: 'rgba(10, 10, 10, 0.8)',
          backdropFilter: 'blur(10px)',
          boxShadow: 'none',
        },
      },
    },
    MuiButton: {
      styleOverrides: {
        root: {
          borderRadius: 8,
          padding: '12px 24px',
        },
      },
    },
  },
});

export const createAppTheme = () => createTheme(getTheme());
