import React, { useEffect, useState } from 'react';
import { motion, useScroll, useTransform, useSpring } from 'framer-motion';
import { Box } from '@mui/material';

interface ParallaxBackgroundProps {
  children: React.ReactNode;
}

export const ParallaxBackground: React.FC<ParallaxBackgroundProps> = ({ children }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const { scrollY } = useScroll();

  const backgroundY = useTransform(scrollY, [0, 1000], [0, 300]);
  const opacity = useTransform(scrollY, [0, 200], [1, 0.3]);

  const springConfig = { stiffness: 100, damping: 30, mass: 1 };
  const mouseX = useSpring(0, springConfig);
  const mouseY = useSpring(0, springConfig);

  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      const { clientX, clientY } = e;
      const { innerWidth, innerHeight } = window;
      const x = (clientX / innerWidth) * 2 - 1;
      const y = (clientY / innerHeight) * 2 - 1;
      setMousePosition({ x, y });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  useEffect(() => {
    mouseX.set(mousePosition.x * 20);
    mouseY.set(mousePosition.y * 20);
  }, [mousePosition, mouseX, mouseY]);

  return (
    <Box sx={{ position: 'relative', width: '100%', minHeight: '100vh', overflow: 'hidden' }}>
      {/* Gradient Background */}
      <motion.div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at center, rgba(29, 78, 216, 0.15), transparent 80%)',
          opacity,
          y: backgroundY,
        }}
      />

      {/* Moving Particles */}
      {Array.from({ length: 20 }).map((_, i) => (
        <motion.div
          key={i}
          style={{
            position: 'fixed',
            top: `${Math.random() * 100}%`,
            left: `${Math.random() * 100}%`,
            width: Math.random() * 100 + 50,
            height: Math.random() * 100 + 50,
            borderRadius: '50%',
            background: 'radial-gradient(circle at center, rgba(99, 102, 241, 0.1), transparent)',
            x: mouseX,
            y: mouseY,
            filter: 'blur(40px)',
            transition: `transform ${Math.random() * 0.5 + 0.5}s cubic-bezier(0.6, 0.05, -0.01, 0.9)`,
          }}
        />
      ))}

      {/* Glass Overlay */}
      <motion.div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backdropFilter: 'blur(100px)',
          background: 'rgba(0, 0, 0, 0.1)',
          opacity: useTransform(scrollY, [0, 300], [0, 0.3]),
        }}
      />

      {/* Content */}
      <motion.div
        style={{
          position: 'relative',
          zIndex: 1,
        }}
      >
        {children}
      </motion.div>
    </Box>
  );
};
