import React, { useState, useRef } from 'react';
import { 
  ThemeProvider, 
  CssBaseline,
  Box,
  Container,
  Typography,
  Paper,
  Tabs,
  Tab,
  AppBar,
  Toolbar,
  IconButton,
  Drawer,
  List,
  ListItem,
  ListItemIcon,
  ListItemText,
  useTheme,
  Divider,
  Switch,
  FormControlLabel,
  Alert,
} from '@mui/material';
import { 
  Menu as MenuIcon,
  FileUpload as FileUploadIcon,
  Timeline as TimelineIcon,
  TableChart as TableChartIcon,
  Assessment as AssessmentIcon,
  Info as InfoIcon,
  Brightness4 as DarkModeIcon,
  Brightness7 as LightModeIcon
} from '@mui/icons-material';
import { motion, useIsPresent } from 'framer-motion';
import { createAppTheme } from './theme/theme';
import { ImmersiveSection } from './components/ImmersiveSection';
import { AnimatedLayout } from './components/AnimatedLayout';
import { ParallaxBackground } from './components/ParallaxBackground';
import FileSelector from './components/FileSelector';
import GraphDisplay from './components/GraphDisplay';
import MatrixDisplay from './components/MatrixDisplay';
import CalendarDisplay from './components/CalendarDisplay';
import CriticalPathDisplay from './components/CriticalPathDisplay';
import GraphControls from './components/GraphControls';
import PDFExport from './components/PDFExport';
import TabPanel from './components/TabPanel';
import { GraphData } from './types/graphTypes';

function App() {
  const [graphData, setGraphData] = useState<GraphData | null>(null);
  const [error, setError] = useState<string | null>(null);
  const [currentTab, setCurrentTab] = useState(0);
  const [drawerOpen, setDrawerOpen] = useState(false);
  const [viewMode, setViewMode] = useState<'timeline' | 'tree'>('timeline');
  const [zoom, setZoom] = useState(1);
  const [showCriticalPath, setShowCriticalPath] = useState(false);
  const graphRef = useRef<HTMLDivElement>(null);
  const matrixRef = useRef<HTMLDivElement>(null);
  const calendarRef = useRef<HTMLDivElement>(null);
  const [darkMode, setDarkMode] = useState(true);
  const isPresent = useIsPresent();

  const theme = createAppTheme();

  const handleFileSelect = async (file: File) => {
    try {
      setError(null);
      const formData = new FormData();
      formData.append('file', file);

      const response = await fetch('http://localhost:5000/api/upload', {
        method: 'POST',
        body: formData,
      });

      if (!response.ok) {
        const errorData = await response.json();
        throw new Error(errorData.error || 'Failed to fetch graph data');
      }

      const data = await response.json();
      setGraphData(data);
      setCurrentTab(1);
    } catch (err) {
      setError(err instanceof Error ? err.message : 'An unknown error occurred');
    }
  };

  const handleTabChange = (event: React.SyntheticEvent, newValue: number) => {
    setCurrentTab(newValue);
  };

  const toggleDrawer = () => {
    setDrawerOpen(!drawerOpen);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const drawerContent = (
    <Box sx={{ width: 250 }} role="presentation">
      <List>
        <ListItem>
          <Typography variant="h6" sx={{ p: 2 }}>
            Menu
          </Typography>
        </ListItem>
        <Divider />
        {['Upload', 'Graph', 'Matrix', 'Calendar', 'About'].map((text, index) => (
          <ListItem 
            button 
            key={text}
            onClick={() => {
              setCurrentTab(index);
              setDrawerOpen(false);
            }}
          >
            <ListItemIcon>
              {index === 0 ? <FileUploadIcon /> :
               index === 1 ? <TimelineIcon /> :
               index === 2 ? <TableChartIcon /> :
               index === 3 ? <AssessmentIcon /> :
               <InfoIcon />}
            </ListItemIcon>
            <ListItemText primary={text} />
          </ListItem>
        ))}
      </List>
      <Divider />
      <FormControlLabel
        control={
          <Switch
            checked={darkMode}
            onChange={toggleDarkMode}
            icon={<LightModeIcon />}
            checkedIcon={<DarkModeIcon />}
          />
        }
        label="Dark Mode"
        sx={{ p: 2 }}
      />
    </Box>
  );

  return (
    <ThemeProvider theme={theme}>
      <CssBaseline />
      <ParallaxBackground>
        <AnimatedLayout isVisible={isPresent}>
          <AppBar 
            position="fixed" 
            sx={{ 
              background: 'rgba(10, 10, 10, 0.5)',
              backdropFilter: 'blur(10px)',
              boxShadow: 'none',
            }}
          >
            <Toolbar>
              <IconButton
                size="large"
                edge="start"
                color="inherit"
                aria-label="menu"
                sx={{ mr: 2 }}
                onClick={toggleDrawer}
              >
                <MenuIcon />
              </IconButton>
              <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                Graph Scheduling Interface
              </Typography>
              <IconButton color="inherit" onClick={toggleDarkMode}>
                {darkMode ? <LightModeIcon /> : <DarkModeIcon />}
              </IconButton>
            </Toolbar>
          </AppBar>

          <Container maxWidth="lg" sx={{ mt: 10 }}>
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              <Box sx={{ borderBottom: 1, borderColor: 'divider', mb: 4 }}>
                <Tabs 
                  value={currentTab} 
                  onChange={handleTabChange}
                  variant="scrollable"
                  scrollButtons="auto"
                  sx={{
                    '& .MuiTab-root': {
                      transition: 'all 0.3s ease',
                      '&:hover': {
                        transform: 'translateY(-2px)',
                        color: 'primary.main',
                      },
                    },
                  }}
                >
                  <Tab label="Upload" icon={<FileUploadIcon />} iconPosition="start" />
                  <Tab label="Graph" icon={<TimelineIcon />} iconPosition="start" />
                  <Tab label="Matrix" icon={<TableChartIcon />} iconPosition="start" />
                  <Tab label="Calendar" icon={<AssessmentIcon />} iconPosition="start" />
                  <Tab label="About" icon={<InfoIcon />} iconPosition="start" />
                </Tabs>
              </Box>

              {error && (
                <motion.div
                  initial={{ opacity: 0, x: -20 }}
                  animate={{ opacity: 1, x: 0 }}
                  exit={{ opacity: 0, x: 20 }}
                >
                  <Alert severity="error" sx={{ mt: 2, mb: 4 }}>
                    {error}
                  </Alert>
                </motion.div>
              )}

              <motion.div
                key={`content-${currentTab}`}
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -50 }}
                transition={{
                  type: "spring",
                  stiffness: 100,
                  damping: 20,
                  mass: 1
                }}
              >
                {currentTab === 0 && (
                  <ImmersiveSection
                    title="Graph Scheduling"
                    subtitle="Upload your file to begin the journey"
                    gradient="linear-gradient(45deg, rgba(33,150,243,0.15), rgba(245,0,87,0.15))"
                  >
                    <FileSelector onFileSelect={handleFileSelect} />
                  </ImmersiveSection>
                )}

                {currentTab === 1 && graphData && (
                  <ImmersiveSection
                    title="Interactive Graph"
                    subtitle="Visualize your project dependencies"
                    gradient="linear-gradient(45deg, rgba(33,150,243,0.15), rgba(245,0,87,0.15))"
                  >
                    <Box sx={{ height: '600px' }} ref={graphRef}>
                      <GraphControls
                        zoom={zoom}
                        onZoomChange={setZoom}
                        onCenter={() => {/* Implement center logic */}}
                        viewMode={viewMode}
                        onViewModeChange={setViewMode}
                        showCriticalPath={showCriticalPath}
                        onToggleCriticalPath={() => setShowCriticalPath(!showCriticalPath)}
                      />
                      <GraphDisplay 
                        data={graphData}
                        zoom={zoom}
                        viewMode={viewMode}
                        showCriticalPath={showCriticalPath}
                      />
                    </Box>
                  </ImmersiveSection>
                )}

                {currentTab === 2 && graphData && (
                  <ImmersiveSection
                    title="Matrix View"
                    subtitle="Analyze dependencies in matrix form"
                    gradient="linear-gradient(45deg, rgba(245,0,87,0.15), rgba(33,150,243,0.15))"
                  >
                    <MatrixDisplay matrix={graphData.matrix} />
                  </ImmersiveSection>
                )}

                {currentTab === 3 && graphData && (
                  <ImmersiveSection
                    title="Calendar and Margins"
                    subtitle="Visualize your project timeline"
                    gradient="linear-gradient(45deg, rgba(33,150,243,0.15), rgba(245,0,87,0.15))"
                  >
                    <CalendarDisplay calendar={graphData.calendar} />
                  </ImmersiveSection>
                )}

                {currentTab === 4 && (
                  <ImmersiveSection
                    title="About"
                    subtitle="Learn more about Graph Scheduling"
                    gradient="linear-gradient(45deg, rgba(245,0,87,0.15), rgba(33,150,243,0.15))"
                  >
                    <Paper
                      sx={{
                        p: 4,
                        textAlign: 'center',
                        maxWidth: 800,
                        mx: 'auto',
                        background: 'rgba(30, 30, 30, 0.5)',
                        backdropFilter: 'blur(10px)',
                        borderRadius: '16px',
                        border: '1px solid rgba(255, 255, 255, 0.1)',
                      }}
                    >
                      <Typography variant="h6" gutterBottom>
                        Graph Scheduling is a powerful tool for project management
                      </Typography>
                      <Typography variant="body1">
                        Visualize dependencies, identify critical paths, and optimize your project timeline
                        with our interactive visualization tools.
                      </Typography>
                    </Paper>
                  </ImmersiveSection>
                )}
              </motion.div>

              {graphData && (
                <motion.div
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ delay: 0.5 }}
                >
                  <Box sx={{ display: 'flex', justifyContent: 'center', mb: 3 }}>
                    <PDFExport
                      graphData={graphData}
                      graphRef={graphRef}
                      matrixRef={matrixRef}
                      calendarRef={calendarRef}
                    />
                  </Box>
                </motion.div>
              )}
            </motion.div>
          </Container>
        </AnimatedLayout>
      </ParallaxBackground>
    </ThemeProvider>
  );
}

export default App;
