    this.height = h;

    const h2 = Math.floor(h/2);
    const h4 = Math.floor(h/4);
    const h8 = Math.floor(h/8);
    const h16 = Math.floor(h/16);

    const w2 = Math.floor(w/2);
    const w4 = Math.floor(w/4);
    const w8 = Math.floor(w/8);
    const w16 = Math.floor(w/16);

