import React, { useEffect, useState } from 'react';
import { motion, useScroll, useTransform } from 'framer-motion';
import { Box } from '@mui/material';
import InteractiveParticles from './InteractiveParticles';

interface ParallaxBackgroundProps {
  children: React.ReactNode;
}

export const ParallaxBackground: React.FC<ParallaxBackgroundProps> = ({ children }) => {
  const [mousePosition, setMousePosition] = useState({ x: 0, y: 0 });
  const { scrollY } = useScroll();

  const backgroundY = useTransform(scrollY, [0, 1000], [0, 300]);
  const opacity = useTransform(scrollY, [0, 200], [1, 0.3]);
  const blur = useTransform(scrollY, [0, 200], [0, 5]);

  useEffect(() => {
    const handleMouseMove = (e: MouseEvent) => {
      const { clientX, clientY } = e;
      const { innerWidth, innerHeight } = window;
      const x = (clientX / innerWidth) * 40 - 20;
      const y = (clientY / innerHeight) * 40 - 20;
      setMousePosition({ x, y });
    };

    window.addEventListener('mousemove', handleMouseMove);
    return () => window.removeEventListener('mousemove', handleMouseMove);
  }, []);

  return (
    <Box sx={{ position: 'relative', width: '100%', minHeight: '100vh', overflow: 'hidden' }}>
      <InteractiveParticles />

      <motion.div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at center, rgba(29, 78, 216, 0.15), transparent 80%)',
          opacity,
          y: backgroundY,
        }}
      />

      {Array.from({ length: 10 }).map((_, i) => (
        <motion.div
          key={i}
          initial={{
            x: Math.random() * window.innerWidth,
            y: Math.random() * window.innerHeight,
          }}
          animate={{
            x: mousePosition.x + Math.random() * window.innerWidth,
            y: mousePosition.y + Math.random() * window.innerHeight,
          }}
          transition={{
            type: "tween",
            ease: "easeOut",
            duration: 0.5
          }}
          style={{
            position: 'fixed',
            width: Math.random() * 300 + 100,
            height: Math.random() * 300 + 100,
            borderRadius: '50%',
            background: `radial-gradient(circle at center, 
              rgba(${Math.random() * 100 + 50}, ${Math.random() * 100 + 50}, ${
              Math.random() * 200 + 50
            }, 0.1), 
              transparent)`,
            filter: 'blur(40px)',
            pointerEvents: 'none'
          }}
        />
      ))}

      <motion.div
        style={{
          position: 'fixed',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          backdropFilter: useTransform(scrollY, [0, 200], ['blur(0px)', 'blur(10px)']),
          background: 'rgba(0, 0, 0, 0.1)',
          opacity: useTransform(scrollY, [0, 300], [0, 0.3]),
        }}
      />

      <motion.div
        style={{
          position: 'relative',
          zIndex: 1,
          filter: useTransform(blur, (value) => `blur(${Math.max(0, value - 5)}px)`),
        }}
      >
        {children}
      </motion.div>
    </Box>
  );
};
