import React from 'react';
import { Box, Paper, Typography } from '@mui/material';
import Network from 'react-vis-network-graph';

interface Node {
  id: string;
  label: string;
  rank: number;
  duration?: number;
  early_date?: number;
  late_date?: number;
  total_margin?: number;
  free_margin?: number;
}

interface Edge {
  from: string;
  to: string;
  label: string;
  value: number;
}

interface GraphDisplayProps {
  graphData: {
    nodes: Node[];
    edges: Edge[];
  };
  criticalPath: string[];
}

const GraphDisplay: React.FC<GraphDisplayProps> = ({ graphData, criticalPath }) => {
  const options = {
    nodes: {
      shape: 'circle',
      font: {
        face: 'NoiGrotesk',
        size: 16
      },
      color: {
        background: '#2C3E50',
        border: '#2C3E50',
        highlight: {
          background: '#34495E',
          border: '#34495E'
        }
      },
      borderWidth: 2,
      size: 30
    },
    edges: {
      arrows: {
        to: { enabled: true, scaleFactor: 1 }
      },
      color: {
        color: '#7F8C8D',
        highlight: '#95A5A6'
      },
      font: {
        face: 'NoiGrotesk',
        size: 12
      },
      width: 2
    },
    physics: {
      enabled: true,
      hierarchicalRepulsion: {
        centralGravity: 0.0,
        springLength: 100,
        springConstant: 0.01,
        nodeDistance: 120,
      }
    },
    layout: {
      hierarchical: {
        enabled: true,
        direction: 'LR',
        sortMethod: 'directed',
        levelSeparation: 200
      }
    }
  };

  // Mettre en évidence le chemin critique
  const enhancedGraphData = {
    nodes: graphData.nodes.map(node => ({
      ...node,
      color: criticalPath.includes(node.id) ? {
        background: '#E74C3C',
        border: '#C0392B',
        highlight: {
          background: '#E74C3C',
          border: '#C0392B'
        }
      } : undefined,
      title: `Tâche ${node.label}
${node.duration ? `Durée: ${node.duration}` : ''}
${node.early_date !== undefined ? `Date au plus tôt: ${node.early_date}` : ''}
${node.late_date !== undefined ? `Date au plus tard: ${node.late_date}` : ''}
${node.total_margin !== undefined ? `Marge totale: ${node.total_margin}` : ''}
${node.free_margin !== undefined ? `Marge libre: ${node.free_margin}` : ''}`
    })),
    edges: graphData.edges.map(edge => ({
      ...edge,
      color: {
        color: criticalPath.includes(edge.from) && criticalPath.includes(edge.to) 
          ? '#E74C3C' 
          : '#7F8C8D'
      }
    }))
  };

  return (
    <Paper elevation={3} sx={{ p: 2, mt: 3 }}>
      <Typography variant="h6" sx={{ mb: 2, fontFamily: 'NoiGrotesk', fontWeight: 'bold' }}>
        Graphe d'ordonnancement
      </Typography>
      <Box sx={{ height: '600px', width: '100%' }}>
        <Network
          graph={enhancedGraphData}
          options={options}
        />
      </Box>
      <Typography variant="body2" sx={{ mt: 2, fontFamily: 'NoiGrotesk', color: '#7F8C8D' }}>
        Note: Le chemin critique est affiché en rouge. Survolez les nœuds pour voir les détails.
      </Typography>
    </Paper>
  );
};

export default GraphDisplay;
